import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/OrderPage.css";
import orderBg from "../img/orderbg.jpg";

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Fetch orders from backend on mount
    const fetchOrders = async () => {
      try {
        const res = await axios.get("/api/orders");
        setOrders(res.data);
      } catch (err) {
        setOrders([]);
      }
    };
    fetchOrders();
  }, []);

  const handleComplete = async (id) => {
    await axios.patch(`/api/orders/${id}`, { status: "Completed" });
    setOrders((prev) =>
      prev.map((order) =>
        order._id === id ? { ...order, status: "Completed" } : order
      )
    );
  };

  return (
    <div
      className="orders-section"
      style={{
        backgroundImage: `url('${orderBg}')`,
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center 2rem",
        backgroundSize: "cover",
        alignContent: "center",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        minHeight: "800px",
        color: "white"
      }}
    >
      <h3>Orders</h3>
      <ul
        style={{
          width: "100%",
          maxWidth: "500px",
          margin: "0 auto",
          textAlign: "center",
        }}
      >
        {orders.length === 0 && <li>No orders yet.</li>}
        {orders.map((order) => (
          <li
            key={order._id}
            style={{
              display: "flex",
              alignItems: "center",
              gap: "1rem",
              justifyContent: "center",
            }}
          >
            <span>
              Table {order.table}: {order.items.join(", ")} - {order.status}
            </span>
            {order.status !== "Completed" && (
              <input
                type="checkbox"
                title="Mark as Completed"
                onChange={() => handleComplete(order._id)}
                style={{ width: "18px", height: "18px" }}
              />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OrdersPage;
