import "../styles/CreateMenu.css";

import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, addDoc, serverTimestamp } from 'firebase/firestore';

// Global variables for Firebase configuration, provided by the Canvas environment
// Referencing them via window to satisfy linters that might not recognize them as global
const appId = typeof window.__app_id !== 'undefined' ? window.__app_id : 'default-app-id';
const firebaseConfig = typeof window.__firebase_config !== 'undefined' ? JSON.parse(window.__firebase_config) : {};
const initialAuthToken = typeof window.__initial_auth_token !== 'undefined' ? window.__initial_auth_token : null;

// Initialize Firebase App outside the component to avoid re-initialization
let app;
let db;
let auth;

try {
  app = initializeApp(firebaseConfig);
  db = getFirestore(app);
  auth = getAuth(app);
} catch (error) {
  console.error("Firebase initialization error:", error);
}

// CreateMenuPage component for adding new food items
function CreateMenuPage({ onBackToMenu }) {
  const [itemName, setItemName] = useState('');
  const [itemPrice, setItemPrice] = useState('');
  const [itemImage, setItemImage] = useState('');
  const [itemDescription, setItemDescription] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [userId, setUserId] = useState(null); // State to store the authenticated user ID

  // Effect for Firebase authentication and user ID setup
  useEffect(() => {
    const setupAuth = async () => {
      try {
        if (auth) {
          // Sign in with custom token if available, otherwise anonymously
          if (initialAuthToken) {
            await signInWithCustomToken(auth, initialAuthToken);
          } else {
            await signInAnonymously(auth);
          }

          // Listen for auth state changes to get the user ID
          onAuthStateChanged(auth, (user) => {
            if (user) {
              setUserId(user.uid);
              console.log("Firebase User ID:", user.uid);
            } else {
              setUserId(null);
              console.log("No Firebase user is signed in.");
            }
          });
        }
      } catch (error) {
        console.error("Firebase authentication error:", error);
        setMessage(`Authentication failed: ${error.message}`);
      }
    };

    setupAuth();
  }, []); // Run only once on component mount

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    // Basic validation
    if (!itemName || !itemPrice || !itemImage || !itemDescription) {
      setMessage('Please fill in all fields.');
      setLoading(false);
      return;
    }

    try {
      if (!db) {
        throw new Error("Firestore is not initialized.");
      }

      // Define the collection path for public data
      const menuCollectionRef = collection(db, `artifacts/${appId}/public/data/menuItems`);

      // Add the new menu item to Firestore
      await addDoc(menuCollectionRef, {
        name: itemName,
        price: parseFloat(itemPrice), // Convert price to a number
        image: itemImage,
        description: itemDescription,
        createdAt: serverTimestamp(), // Add a timestamp
        createdBy: userId // Store the user ID who created the item
      });

      setMessage('Menu item added successfully!');
      // Clear form fields
      setItemName('');
      setItemPrice('');
      setItemImage('');
      setItemDescription('');
    } catch (error) {
      console.error("Error adding document: ", error);
      setMessage(`Failed to add menu item: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4 sm:p-6 lg:p-8 font-inter">
      <h1 className="text-4xl sm:text-5xl font-extrabold text-center text-gray-800 mb-8 sm:mb-12 rounded-lg py-4 bg-white shadow-md">
        Create New Menu Item
      </h1>

      {/* Display current user ID */}
      {userId && (
        <div className="text-center text-gray-700 mb-4">
          Current User ID: <span className="font-semibold">{userId}</span>
        </div>
      )}

      <div className="max-w-xl mx-auto bg-white p-6 sm:p-8 rounded-xl shadow-lg">
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Item Name */}
          <div>
            <label htmlFor="itemName" className="block text-lg font-medium text-gray-700 mb-1">
              Food Name
            </label>
            <input
              type="text"
              id="itemName"
              value={itemName}
              onChange={(e) => setItemName(e.target.value)}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-base"
              placeholder="e.g., Spicy Chicken Wings"
              required
            />
          </div>

          {/* Item Price */}
          <div>
            <label htmlFor="itemPrice" className="block text-lg font-medium text-gray-700 mb-1">
              Price ($)
            </label>
            <input
              type="number"
              id="itemPrice"
              value={itemPrice}
              onChange={(e) => setItemPrice(e.target.value)}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-base"
              placeholder="e.g., 12.99"
              step="0.01" // Allow decimal values
              required
            />
          </div>

          {/* Image URL */}
          <div>
            <label htmlFor="itemImage" className="block text-lg font-medium text-gray-700 mb-1">
              Image URL
            </label>
            <input
              type="url" // Use type="url" for image URLs
              id="itemImage"
              value={itemImage}
              onChange={(e) => setItemImage(e.target.value)}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-base"
              placeholder="e.g., https://example.com/burger.jpg"
              required
            />
            {/* Optional: Image preview */}
            {itemImage && (
              <div className="mt-4 flex justify-center">
                <img src={itemImage} alt="Preview" className="max-w-full h-32 object-cover rounded-md shadow-sm" />
              </div>
            )}
          </div>

          {/* Item Description */}
          <div>
            <label htmlFor="itemDescription" className="block text-lg font-medium text-gray-700 mb-1">
              Description
            </label>
            <textarea
              id="itemDescription"
              value={itemDescription}
              onChange={(e) => setItemDescription(e.target.value)}
              rows="4"
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-base"
              placeholder="A brief description of the food item..."
              required
            ></textarea>
          </div>

          {/* Submission Button */}
          <button
            type="submit"
            className="w-full bg-green-600 text-white py-3 rounded-md font-semibold text-lg hover:bg-green-700 transition-colors duration-200 shadow-md flex items-center justify-center"
            disabled={loading} // Disable button while loading
          >
            {loading ? (
              <svg className="animate-spin h-5 w-5 text-white mr-3" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
            ) : (
              'Add Menu Item'
            )}
          </button>

          {/* Message display for success/error */}
          {message && (
            <p className={`mt-4 text-center text-sm font-medium ${message.includes('successfully') ? 'text-green-600' : 'text-red-600'}`}>
              {message}
            </p>
          )}
        </form>

        {/* Back to Menu button */}
        <button
          onClick={onBackToMenu}
          className="mt-6 w-full bg-gray-300 text-gray-800 py-2 rounded-md font-semibold text-lg hover:bg-gray-400 transition-colors duration-200 shadow-md"
        >
          Back to Menu
        </button>
      </div>
    </div>
  );
}

export default CreateMenuPage;
