import React from "react";
import "../styles/OrderPage.css";

// Add image import or use direct URL
const orderImgUrl = "https://cdn-icons-png.flaticon.com/512/1046/1046784.png";

const OrdersPage = ({ orders, setOrders }) => {
  const handleComplete = (id) => {
    setOrders((prev) =>
      prev
        .map((order) =>
          order.id === id ? { ...order, status: "Completed" } : order
        )
        .filter((order) => order.id !== id) // Remove the completed order
    );
  };

  return (
    <div className="orders-section">
      {/* Add order image at the top */}
      <div style={{ textAlign: "center", marginBottom: "1rem" }}>
        <img
          src={orderImgUrl}
          alt="Order"
          style={{ width: "90px", height: "90px", objectFit: "contain" }}
        />
      </div>
      <h3>Orders</h3>
      <ul>
        {orders.length === 0 && <li>No orders yet.</li>}
        {orders.map((order) => (
          <li key={order.id} style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
            <span>
              Table {order.table}: {order.items.join(", ")} - {order.status}
            </span>
            {order.status !== "Completed" && (
              <input
                type="checkbox"
                title="Mark as Completed"
                onChange={() => handleComplete(order.id)}
                style={{ width: "18px", height: "18px" }}
              />
            )}
            {/* The checkmark will not be shown since completed orders are removed */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OrdersPage;
