import React from "react";
import "./CustomerMenu.css";
import Footer from "./footer";

const menuCards = [
  {
    id: 1,
    img: "https://cdn.pixabay.com/photo/2021/09/20/06/55/spaghetti-6639970__340.jpg",
    name: "Spaghetti",
    originalPrice: 199,
    price: 149,
  },
  {
    id: 2,
    img: "https://cdn.pixabay.com/photo/2017/05/07/08/56/pancakes-2291908__340.jpg",
    name: "Pancakes",
    originalPrice: 199,
    price: 149,
  },
  {
    id: 3,
    img: "https://cdn.pixabay.com/photo/2014/11/05/15/57/salmon-518032__340.jpg",
    name: "Salmon",
    originalPrice: 199,
    price: 149,
  },
];

const CustomerMenu = ({ onBack }) => {
  return (
    <div>
    <div className="customer-menu-container">
      <section className="menu-cards-section">
        {menuCards.map((item) => (
          <article key={item.id} className="menu-card">
            <img src={item.img} alt={item.name} />
            <h3>{item.name}</h3>
            <span className="span1">
              Original Price: <b>{item.originalPrice}/-</b>
            </span>
            <span>
              Price: <b>{item.price}/-</b>
            </span>
            <button className="order-now-btn" type="button">
              Order Now
            </button>
          </article>
        ))}
      </section>
      <button onClick={onBack} className="back-btn">
        Back
      </button>
    </div>
    <Footer />
    </div>
  );
};

export default CustomerMenu;
