import React, { useState } from "react";
import "./Dashboard.css";
import NavBar from "./NavBar";
import CreateMenuPage from "./CreateMenuPage";
import GenerateQRCodePage from "./GenerateQRCodePage";
import OrdersPage from "./OrdersPage";

const Dashboard = ({ menu, setMenu, orders, setOrders }) => {
  const [activeSection, setActiveSection] = useState("menu");

  return (
    <div className="dashboard-container">
      <NavBar />
      <h2>Hotel Admin Dashboard</h2>
      <div className="dashboard-buttons">
        <button
          className={activeSection === "menu" ? "active" : ""}
          onClick={() => setActiveSection("menu")}
        >
          Create Menu
        </button>
        <button
          className={activeSection === "qrcode" ? "active" : ""}
          onClick={() => setActiveSection("qrcode")}
        >
          Generate QR Code
        </button>
        <button
          className={activeSection === "orders" ? "active" : ""}
          onClick={() => setActiveSection("orders")}
        >
          Orders
        </button>
      </div>

      {activeSection === "menu" && (
        <CreateMenuPage menu={menu} setMenu={setMenu} />
      )}
      {activeSection === "qrcode" && <GenerateQRCodePage />}
      {activeSection === "orders" && <OrdersPage orders={orders} />}
    </div>
  );
};

export default Dashboard;
