import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/OrderPage.css";
import orderBg from "../img/orderbg.jpg";

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Fetch orders from backend on mount
    const fetchOrders = async () => {
      try {
        const res = await axios.get("/api/orders");
        console.log("Orders API response:", res.data);

        // Try to handle all possible backend shapes
        let ordersArr = [];
        if (Array.isArray(res.data)) {
          ordersArr = res.data;
        } else if (res.data && Array.isArray(res.data.orders)) {
          ordersArr = res.data.orders;
        } else if (res.data && res.data.data && Array.isArray(res.data.data)) {
          ordersArr = res.data.data;
        } else if (res.data && typeof res.data === "object") {
          // Try to extract any array property
          const arrKey = Object.keys(res.data).find(
            k => Array.isArray(res.data[k])
          );
          if (arrKey) ordersArr = res.data[arrKey];
        }
        setOrders(ordersArr);
      } catch (err) {
        setOrders([]);
        console.error("Failed to fetch orders:", err);
      }
    };
    fetchOrders();
  }, []);

  const handleComplete = async (id) => {
    await axios.patch(`/api/orders/${id}`, { status: "Completed" });
    setOrders((prev) =>
      prev.map((order) =>
        order._id === id ? { ...order, status: "Completed" } : order
      )
    );
  };

  return (
    <div
      className="orders-section"
      style={{
        backgroundImage: `url('${orderBg}')`,
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center 2rem",
        backgroundSize: "cover",
        alignContent: "center",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        minHeight: "800px",
        color: "white"
      }}
    >
      <h3>Orders</h3>
      <ul
        style={{
          width: "100%",
          maxWidth: "500px",
          margin: "0 auto",
          textAlign: "center",
        }}
      >
        {/* Show only the 5 most recent orders */}
        {orders.length === 0 && <li>No orders yet.</li>}
        {orders
          .slice(-5)
          .reverse()
          .map((order) => (
            <li
              key={order._id || order.id}
              style={{
                display: "flex",
                alignItems: "center",
                gap: "1rem",
                justifyContent: "center",
              }}
            >
              <span>
                Table {order.table}: {Array.isArray(order.items) ? order.items.join(", ") : order.items} - {order.status}
              </span>
              {order.status !== "Completed" && (
                <input
                  type="checkbox"
                  title="Mark as Completed"
                  onChange={() => handleComplete(order._id || order.id)}
                  style={{ width: "18px", height: "18px" }}
                />
              )}
            </li>
          ))}
      </ul>
    </div>
  );
};

export default OrdersPage;
