import React from "react";
import "../styles/Dashboard.css";
import NavBar from "./NavBar";
import GenerateQRCodePage from "./GenerateQRCodePage"; // Add this import

const Dashboard = ({ menu, setMenu, orders, setOrders, onAddMenuItem, onRemoveMenuItem }) => {
  // Pass handlePlaceOrder to NavBar
  const handlePlaceOrder = (order) => {
    setOrders((prev) => [
      ...prev,
      {
        ...order,
        id: Date.now(),
        status: "Pending",
        table: order.table || "N/A",
      },
    ]);
  };

  return (
    <div className="dashboard-container">
      <NavBar
        menu={menu}
        setMenu={setMenu}
        orders={orders}
        setOrders={setOrders}
        onAddMenuItem={onAddMenuItem}
        onRemoveMenuItem={onRemoveMenuItem}
        onPlaceOrder={handlePlaceOrder}
      />
      {/* Add QR Code Analytics & Insights below the NavBar */}
      <div style={{ marginTop: "2rem" }}>
        <GenerateQRCodePage />
      </div>
    </div>
  );
};

export default Dashboard;
