import React, { useState } from "react";
import HomePage from "./components/HomePage";
import CustomerMenu from "./components/CustomerMenu";
import LoginPage from "./components/LoginPage";
import SignupPage from "./components/SignupPage";
import Dashboard from "./components/Dashboard";
import "./styles/LoginSignup.css";

const initialMenu = [
  { id: 1, name: "Paneer Butter Masala", price: 250 },
  { id: 2, name: "Veg Biryani", price: 180 },
];

function App() {
  const [page, setPage] = useState("home");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [menu, setMenu] = useState(initialMenu);
  const [orders, setOrders] = useState([]);

  const handlePlaceOrder = (order) => {
    setOrders((prev) => [
      ...prev,
      { ...order, id: Date.now(), status: "Pending" },
    ]);
  };

  if (isLoggedIn) {
    return (
      <Dashboard
        menu={menu}
        setMenu={setMenu}
        orders={orders}
        setOrders={setOrders}
      />
    );
  }

  if (page === "menu") {
    return (
      <CustomerMenu
        menu={menu}
        onBack={() => setPage("home")}
        onPlaceOrder={handlePlaceOrder}
      />
    );
  }

  return (
    <div className="App">
      {page === "home" && (
        <HomePage
          onViewMenu={() => setPage("menu")}
          onLogin={() => setPage("LoginPage")}
          onSignup={() => setPage("SignupPage")}
        />
      )}
      {page === "LoginPage" && (
        <LoginPage onSwitch={setPage} onLogin={() => setIsLoggedIn(true)} />
      )}
      {page === "SignupPage" && (
        <SignupPage onSwitch={setPage} onSignup={() => setIsLoggedIn(true)} />
      )}
    </div>
  );
}

export default App;
