import "../styles/CreateMenu.css";

import React, { useState } from 'react';

// CreateMenuPage component for adding new food items
function CreateMenuPage({ onBackToMenu, onAddItem }) {
  const [itemName, setItemName] = useState('');
  const [itemPrice, setItemPrice] = useState('');
  const [itemImage, setItemImage] = useState('');
  const [message, setMessage] = useState('');

  // Removed: State for QR Code generation
  // const [qrDataInput, setQrDataInput] = useState('');
  // const [generatedQrImage, setGeneratedQrImage] = useState('');
  // const [qrMessage, setQrMessage] = useState('');

  // Function to handle form submission for adding menu item
  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage('');

    // Basic validation
    if (!itemName || !itemPrice || !itemImage) {
      setMessage('Please fill in all fields.');
      return;
    }

    // Create a new item object
    const newItem = {
      id: Date.now(), // Simple unique ID generation
      name: itemName,
      price: parseFloat(itemPrice), // Convert price to a number
      image: itemImage,
    };

    // Call the onAddItem prop to pass the new item to the parent
    onAddItem(newItem);

    setMessage('Menu item added successfully!');
    // Clear form fields
    setItemName('');
    setItemPrice('');
    setItemImage('');
  };

  return (
    <div className="main-menu">
     

      <h1 className="header1">
        Create New Menu Item
      </h1>

      <div className="max-w-xl mx-auto bg-white p-6 sm:p-8 rounded-xl shadow-lg">
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Item Name */}
          <div>
            <label htmlFor="itemName" className="block text-lg font-medium text-gray-700 mb-1">
              Food Name
            </label>
            <input
              type="text"
              id="itemName"
              value={itemName}
              onChange={(e) => setItemName(e.target.value)}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-base"
              placeholder="e.g., Spicy Chicken Wings"
              required
            />
          </div>

          {/* Item Price */}
          <div>
            <label htmlFor="itemPrice" className="block text-lg font-medium text-gray-700 mb-1">
              Price (₹)
            </label>
            <input
              type="number"
              id="itemPrice"
              value={itemPrice}
              onChange={(e) => setItemPrice(e.target.value)}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-base"
              placeholder="e.g., 299.00"
              step="0.01" // Allow decimal values
              required
            />
          </div>

          {/* Image URL */}
          <div>
            <label htmlFor="itemImage" className="block text-lg font-medium text-gray-700 mb-1">
              Image URL
            </label>
            <input
              type="url" // Use type="url" for image URLs
              id="itemImage"
              value={itemImage}
              onChange={(e) => setItemImage(e.target.value)}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-base"
              placeholder="e.g., https://example.com/burger.jpg"
              required
            />
            {/* Optional: Image preview */}
            {itemImage && (
              <div className="mt-4 flex justify-center">
                <img src={itemImage} alt="Preview" className="max-w-full h-32 object-cover rounded-md shadow-sm" />
              </div>
            )}
          </div>

          {/* Submission Button */}
          <button
            type="submit"
            className="w-full bg-green-600 text-white py-3 rounded-md font-semibold text-lg hover:bg-green-700 transition-colors duration-200 shadow-md flex items-center justify-center"
          >
            Add Menu Item
          </button>

          {/* Message display for success/error */}
          {message && (
            <p className={`mt-4 text-center text-sm font-medium ${message.includes('successfully') ? 'text-green-600' : 'text-red-600'}`}>
              {message}
            </p>
          )}
        </form>

        {/* Back to Menu button */}
        <button
          onClick={onBackToMenu}
          className="mt-6 w-full bg-gray-300 text-gray-800 py-2 rounded-md font-semibold text-lg hover:bg-gray-400 transition-colors duration-200 shadow-md"
        >
          Back to Menu
        </button>
      </div>


    </div>
  );
}

export default CreateMenuPage;
