import React, { useState } from 'react';

// In your main CSS file (e.g., index.css), you would import the font:
// @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');
// And ensure your tailwind.config.js is set up to use 'Inter' as the default font family.

// --- Reusable Components ---

const HeroSection = () => (
    <section className="text-center py-20 px-4 bg-white">
        <div className="max-w-3xl mx-auto">
            <h1 className="text-4xl md:text-5xl font-bold text-stone-900 mb-4 tracking-tight">The Future of Dining is Just a Scan Away</h1>
            <p className="text-lg md:text-xl text-stone-600 max-w-2xl mx-auto">
                We're seamlessly connecting restaurants and their patrons through elegant, intuitive technology, making the dining experience more efficient and enjoyable for everyone.
            </p>
        </div>
    </section>
);

const BenefitCard = ({ title, description, colorClass }) => (
    <div className="bg-white p-6 rounded-xl shadow-sm">
        <h3 className={`font-bold text-xl mb-2 ${colorClass}`}>{title}</h3>
        <p className="text-stone-600">{description}</p>
    </div>
);

const HowItWorksStep = ({ number, title, description }) => (
     <div className="relative flex flex-col items-center p-6 bg-stone-50 rounded-xl">
        <div className="flex items-center justify-center w-16 h-16 rounded-full bg-orange-500 text-white text-2xl font-bold mb-4 z-10">{number}</div>
        <h3 className="text-xl font-semibold mb-2">{title}</h3>
        <p className="text-stone-600">{description}</p>
    </div>
);


// --- Main Page Sections as Components ---

const BenefitsSection = () => {
    const [activeTab, setActiveTab] = useState('restaurants');

    const restaurantBenefits = [
        { title: 'Create & Customize with Ease', description: 'Build a beautiful, dynamic digital menu in minutes. Add mouth-watering photos, detailed descriptions, and update prices or availability instantly.' },
        { title: 'Reduce Costs & Waste', description: 'Say goodbye to the endless cycle of printing, reprinting, and laminating paper menus. Save money and be more eco-friendly.' },
        { title: 'Increase Efficiency', description: 'Receive clear, accurate online orders directly from the table to your kitchen, reducing errors and freeing up your staff to focus on service.' },
        { title: 'Delight Your Customers', description: 'Offer a modern, convenient, and contactless experience that today\'s diners expect, leading to higher satisfaction and return visits.' },
    ];

    const dinerBenefits = [
        { title: 'Instant Access', description: 'Simply scan the QR code on your table to instantly pull up the complete, up-to-date menu on your own device.' },
        { title: 'Browse Visually', description: 'See what looks good with high-quality, enticing images of every dish, helping you choose your next favorite meal.' },
        { title: 'Order on Your Time', description: 'Ready to order? Place it directly from your phone. Need another drink? Just a few taps away. No more waiting.' },
        { title: 'Safe & Simple', description: 'Enjoy a completely contactless ordering and payment process for a safer, more convenient, and worry-free dining experience.' },
    ];

    const tabButtonStyles = "w-1/2 py-2 px-4 rounded-full text-sm md:text-base font-semibold transition-colors duration-300";
    const activeTabStyles = "bg-orange-500 text-white";
    const inactiveTabStyles = "bg-transparent text-stone-600 hover:bg-stone-300/50";

    return (
        <section className="py-16 md:py-24 px-4">
            <div className="max-w-4xl mx-auto">
                <div className="text-center mb-12">
                    <h2 className="text-3xl font-bold text-stone-900">A Smarter Way to Serve & Dine</h2>
                    <p className="mt-3 text-lg text-stone-600">Discover the benefits for your business and your customers.</p>
                </div>

                <div className="flex justify-center mb-8 rounded-full p-1 bg-stone-200/80 max-w-sm mx-auto">
                    <button onClick={() => setActiveTab('restaurants')} className={`${tabButtonStyles} ${activeTab === 'restaurants' ? activeTabStyles : inactiveTabStyles}`}>For Restaurants</button>
                    <button onClick={() => setActiveTab('diners')} className={`${tabButtonStyles} ${activeTab === 'diners' ? activeTabStyles : inactiveTabStyles}`}>For Diners</button>
                </div>

                <div className="transition-opacity duration-300 ease-in-out">
                    {activeTab === 'restaurants' ? (
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                            {restaurantBenefits.map(b => <BenefitCard key={b.title} {...b} colorClass="text-orange-600" />)}
                        </div>
                    ) : (
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                            {dinerBenefits.map(b => <BenefitCard key={b.title} {...b} colorClass="text-green-600" />)}
                        </div>
                    )}
                </div>
            </div>
        </section>
    );
};

const HowItWorksSection = () => (
    <section className="py-16 md:py-24 px-4 bg-white">
        <div className="max-w-5xl mx-auto">
            <div className="text-center mb-12">
                <h2 className="text-3xl font-bold text-stone-900">How It Works</h2>
                <p className="mt-3 text-lg text-stone-600">A seamless experience from scan to satisfaction.</p>
            </div>
            <div className="relative grid grid-cols-1 md:grid-cols-3 gap-8 text-center">
                <div className="absolute top-1/2 left-0 w-full h-0.5 bg-stone-200 hidden md:block" style={{ transform: 'translateY(-50%)', top: '2rem' }}></div>
                <HowItWorksStep number="1" title="Scan the QR Code" description="Customers use their smartphone camera to scan the unique QR code at their table." />
                <HowItWorksStep number="2" title="Browse & Order" description="The interactive menu appears instantly. They can browse, select items, and place their order." />
                <HowItWorksStep number="3" title="Enjoy the Meal" description="The order is sent directly to the kitchen, and food is served. It's that simple!" />
            </div>
        </div>
    </section>
);

const OurStorySection = () => (
    <section className="py-16 md:py-24 px-4">
        <div className="max-w-4xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12 items-center">
            <div className="text-center md:text-left">
                <h2 className="text-3xl font-bold text-stone-900 mb-4">Our Story</h2>
                <p className="text-stone-600 mb-4">We're a team of foodies, tech geeks, and hospitality veterans. The idea was born over a meal where we spent more time trying to order than enjoying our food. We decided to combine our passions to build a solution that we, and our favorite restaurants, would genuinely love to use.</p>
                <p className="text-stone-600">We believe that technology should enhance life's best moments, and a shared meal is one of the most important.</p>
            </div>
            <div className="bg-stone-200 h-64 md:h-80 rounded-xl flex items-center justify-center">
                <span className="text-stone-500">Your team photo here</span>
            </div>
        </div>
    </section>
);

const Footer = () => (
    <footer className="bg-stone-800 text-white">
        <div className="max-w-4xl mx-auto py-16 px-4 text-center">
            <h2 className="text-3xl font-bold mb-4">Join Us in Revolutionizing the Dining Experience</h2>
