import React, { useEffect } from "react";
import "../styles/AboutUs.css";

const Aboutus = () => {
  useEffect(() => {
    // Tab switching logic for benefits section
    const btnRestaurants = document.getElementById('btn-restaurants');
    const btnDiners = document.getElementById('btn-diners');
    const contentRestaurants = document.getElementById('content-restaurants');
    const contentDiners = document.getElementById('content-diners');

    function setActiveTab(activeBtn, activeContent, inactiveBtn, inactiveContent) {
      activeBtn.classList.add('active');
      inactiveBtn.classList.remove('active');
      activeContent.classList.remove('hidden');
      inactiveContent.classList.add('hidden');
    }

    if (btnRestaurants && btnDiners && contentRestaurants && contentDiners) {
      btnRestaurants.onclick = () => {
        setActiveTab(btnRestaurants, contentRestaurants, btnDiners, contentDiners);
      };
      btnDiners.onclick = () => {
        setActiveTab(btnDiners, contentDiners, btnRestaurants, contentRestaurants);
      };
      btnRestaurants.click();
    }
    // Cleanup
    return () => {
      if (btnRestaurants) btnRestaurants.onclick = null;
      if (btnDiners) btnDiners.onclick = null;
    };
  }, []);

  return (
    <main className="w-full bg-stone-50 text-stone-800" style={{ fontFamily: "'Inter', sans-serif" }}>
      {/* Hero Section */}
      <section className="text-center py-20 px-4 bg-white">
        <div className="max-w-3xl mx-auto">
          <h1 className="text-4xl md:text-5xl font-bold text-stone-900 mb-4 tracking-tight">
            The Future of Dining is Just a Scan Away
          </h1>
          <p className="text-lg md:text-xl text-stone-600 max-w-2xl mx-auto">
            We're seamlessly connecting restaurants and their patrons through elegant, intuitive technology, making the dining experience more efficient and enjoyable for everyone.
          </p>
        </div>
      </section>

      {/* Interactive Benefits Section */}
      <section className="py-16 md:py-24 px-4">
        <div className="max-w-4xl mx-auto">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-stone-900">A Smarter Way to Serve & Dine</h2>
            <p className="mt-3 text-lg text-stone-600">Discover the benefits for your business and your customers.</p>
          </div>
          <div className="flex justify-center mb-8 rounded-full p-1 bg-stone-200/80 max-w-sm mx-auto">
            <button id="btn-restaurants" className="tab-button w-1/2 py-2 px-4 rounded-full text-sm md:text-base font-semibold transition-colors duration-300">For Restaurants</button>
