function App() {
  const [page, setPage] = useState("home");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <div className="App">
      {page === "home" && (
        <HomePage
          onViewMenu={() => setPage("menu")}
          onLogin={() => {
            setIsLoggedIn(true);
            setPage("dashboard"); // Redirect to dashboard after login
          }}
          onSignup={() => setPage("SignupPage")}
        />
      )}
      {page === "LoginPage" && (
        <LoginPage
          onSwitch={setPage}
          onLogin={() => {
            setIsLoggedIn(true);
            setPage("dashboard"); // Redirect to dashboard after login
          }}
        />
      )}
      {page === "dashboard" && (
        <Dashboard />
      )}
      {page === "menu" && (
        <CustomerMenu />
      )}
      {/* ...other routes/pages... */}
    </div>
  );
}
