import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/CustomerMenu.css";

const BACKEND_URL = "http://localhost:5000"; // Make sure this matches your backend

const CustomerMenu = ({ menu: initialMenu = [], onBack, onPlaceOrder, userId }) => {
  const [menu, setMenu] = useState(initialMenu);
  const [table, setTable] = useState("");

  // Fetch menu for customer if userId is provided
  useEffect(() => {
    if (userId) {
      axios.get("/api/menu", { params: { userId } })
        .then(res => setMenu(res.data))
        .catch(() => setMenu([]));
    }
  }, [userId]);

  const handleOrder = (item) => {
    if (!table) {
      alert("Please enter your table number.");
      return;
    }
    // Confirmation popup before placing order
    const confirmOrder = window.confirm(`Place order for "${item.name}" at table ${table}?`);
    if (!confirmOrder) return;
    onPlaceOrder &&
      onPlaceOrder({
        items: [item.name],
        menuItemId: item._id || item.id,
        status: "Pending",
        table,
      });
  };

  // Add debug logging and show image URL in UI
  const getImageUrl = (image) => {
    if (!image) return null;
    if (image.startsWith("http://") || image.startsWith("https://")) {
      return image;
    }
    let relPath = image.replace(/^\/?uploads\//, "");
    const parts = relPath.split("/");
    const filename = encodeURIComponent(parts.pop());
    const folder = parts.length ? parts.join("/") + "/" : "";
    const url = `${BACKEND_URL}/uploads/${folder}${filename}`;
    return url;
  };

  const [bookOpen, setBookOpen] = useState(false);

  return (
    <div className="customer-menu-container">
      {/* Book Opening Effect */}
      <div style={{ display: "flex", justifyContent: "center", margin: "2rem 0" }}>
        <style>
          {`
          .book {
              width: 300px;
              height: 400px;
              position: relative;
              perspective: 1500px;
          }
          .cover {
              width: 100%;
              height: 100%;
              position: absolute;
              top: 0;
              left: 0;
              transform-style: preserve-3d;
              transform-origin: left center;
              transition: transform 2s cubic-bezier(0.68, -0.55, 0.27, 1.55); /* Slower */
              z-index: 3;
          }
          .page-1 {
              width: 100%;
              height: 100%;
              position: absolute;
              top: 0;
              left: 0;
              transform-style: preserve-3d;
              transform-origin: right center;
              transition: transform 2s cubic-bezier(0.68, -0.55, 0.27, 1.55); /* Slower */
              transition-delay: 0.2s;
              z-index: 2;
          }
          .final-page {
              width: 100%;
              height: 100%;
              position: absolute;
              top: 0;
              left: 0;
              background-color: #fff;
              box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
              border-radius: 0 6px 6px 0;
              padding: 30px;
              box-sizing: border-box;
              z-index: 1;
          }
          .book.open .cover {
              transform: rotateY(-180deg);
          }
          .book.open .page-1 {
              transform: rotateY(180deg);
          }
          .face {
              width: 100%;
              height: 100%;
              position: absolute;
              top: 0;
              left: 0;
              padding: 30px;
              box-sizing: border-box;
              backface-visibility: hidden;
              display: flex;
              flex-direction: column;
              justify-content: center;
              align-items: center;
              border-radius: 6px;
          }
          .cover-front {
              background: linear-gradient(45deg, #6a11cb 0%, #2575fc 100%);
              color: white;
              box-shadow: 0 10px 30px rgba(0,0,0,0.3);
              border-radius: 0 6px 6px 0;
          }
          .cover-back {
              background-color: #fff;
              transform: rotateY(180deg);
              border-radius: 6px 0 0 6px;
              box-shadow: inset 5px 0px 15px -5px rgba(0,0,0,0.2);
          }
          .page-1-front {
               background-color: #fff;
               border-radius: 0 6px 6px 0;
          }
          .page-1-back {
              background-color: #fff;
              transform: rotateY(180deg);
              border-radius: 6px 0 0 6px;
              box-shadow: inset 5px 0px 15px -5px rgba(0,0,0,0.2);
          }
          .cover-title {
              font-size: 2rem;
              font-weight: 700;
              text-shadow: 2px 2px 5px rgba(0,0,0,0.2);
          }
          `}
        </style>
        <div
          className={`book${bookOpen ? " open" : ""}`}
          onClick={() => setBookOpen(true)}
          style={{ cursor: bookOpen ? "default" : "pointer" }}
        >
          <div className="final-page">
            <h2>Chapter 4</h2>
            <p>The story concludes here... for now.</p>
          </div>
          <div className="page-1">
            <div className="face page-1-front">
              <h2>Chapter 2</h2>
              <p>This page was under the cover. It flips to the right to reveal the next part of the story.</p>
            </div>
            <div className="face page-1-back">
              <h2>Chapter 3</h2>
              <p>This is the back of the second page, now resting on the left.</p>
            </div>
          </div>
          <div className="cover">
            <div className="face cover-front">
              <div className="cover-title">The Magic of CSS</div>
              <p>By A. Developer</p>
            </div>
            <div className="face cover-back">
              <h2>Chapter 1</h2>
              <p>This is the back of the cover, which becomes the first page on the left.</p>
            </div>
          </div>
        </div>
      </div>
      <div style={{ textAlign: "center", marginBottom: "1rem" }}>
        <label>
          Table Number:{" "}
          <input
            type="text"
            value={table}
            onChange={(e) => setTable(e.target.value)}
            placeholder="Enter table number"
            style={{
              padding: "0.3rem",
              borderRadius: "4px",
              border: "1px solid #ccc",
            }}
          />
        </label>
      </div>
      <section className="menu-cards-section">
        {menu.length === 0 && (
          <div style={{ textAlign: "center", width: "100%" }}>
            No menu items available.
          </div>
        )}
        {menu.map((item) => {
          const imgUrl = getImageUrl(item.image);
          return (
            <article key={item._id || item.id} className="menu-card">
              <img
                src={imgUrl || "https://placehold.co/180x120?text=No+Image"}
                alt={item.name}
                style={{
                  width: "180px",
                  height: "120px",
                  objectFit: "cover",
                  borderRadius: "8px",
                }}
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = "https://placehold.co/180x120?text=No+Image";
                }}
              />
              <h3>{item.name}</h3>
              <span>
                Price: <b>{item.price}/-</b>
              </span>
              <button
                className="order-now-btn"
                type="button"
                onClick={() => handleOrder(item)}
              >
                Order Now
              </button>
            </article>
          );
        })}
      </section>
    </div>
  );
};

export default CustomerMenu;
