import React from "react";
import "../styles/Dashboard.css";
import NavBar from "./NavBar";

// Add Analytics component
const AnalyticsInsights = () => (
  <div
    style={{
      margin: "2.5rem auto",
      background: "#fff",
      borderRadius: 8,
      boxShadow: "0 2px 12px rgba(0,0,0,0.07)",
      padding: "2rem",
      maxWidth: 600,
      color: "#222",
    }}
  >
    <h3 style={{ color: "#2575fc", marginBottom: "1.2rem" }}>Analytics & Insights</h3>
    <div style={{ marginBottom: "1.5rem" }}>
      <b>Most Viewed Categories/Dishes</b>
      <ul style={{ margin: "0.5rem 0 0 1.2rem" }}>
        <li>• Pizza (120 views)</li>
        <li>• Burgers (98 views)</li>
        <li>• Pasta (85 views)</li>
      </ul>
    </div>
    <div style={{ marginBottom: "1.5rem" }}>
      <b>Peak Scan Times</b>
      <ul style={{ margin: "0.5rem 0 0 1.2rem" }}>
        <li>• 12:00 PM - 2:00 PM</li>
        <li>• 7:00 PM - 9:00 PM</li>
      </ul>
    </div>
    <div>
      <b>Customer Interaction Heatmap</b>
      <div
        style={{
          marginTop: "0.7rem",
          width: "100%",
          height: 80,
          background:
            "linear-gradient(90deg, #f97316 30%, #facc15 60%, #4ade80 100%)",
          borderRadius: 6,
          position: "relative",
        }}
      >
        <span
          style={{
            position: "absolute",
            left: 10,
            top: 10,
            color: "#fff",
            fontWeight: "bold",
            fontSize: 12,
          }}
        >
          Low
        </span>
        <span
          style={{
            position: "absolute",
            right: 10,
            top: 10,
            color: "#fff",
            fontWeight: "bold",
            fontSize: 12,
          }}
        >
          High
        </span>
        {/* This is a placeholder. Replace with a real heatmap/chart for production. */}
      </div>
    </div>
  </div>
);

const Dashboard = ({ menu, setMenu, orders, setOrders, onAddMenuItem, onRemoveMenuItem }) => {
  // Pass handlePlaceOrder to NavBar
  const handlePlaceOrder = (order) => {
    setOrders((prev) => [
      ...prev,
      {
        ...order,
        id: Date.now(),
        status: "Pending",
        table: order.table || "N/A",
      },
    ]);
  };

  return (
    <div className="dashboard-container">
      <NavBar
        menu={menu}
        setMenu={setMenu}
        orders={orders}
        setOrders={setOrders}
        onAddMenuItem={onAddMenuItem}
        onRemoveMenuItem={onRemoveMenuItem}
        onPlaceOrder={handlePlaceOrder}
      />
      <AnalyticsInsights />
    </div>
  );
};

export default Dashboard;
