// Removed: import "../styles/CreateMenu.css"; // Styles are now embedded below

import React, { useState, useEffect } from 'react';
import axios from "axios";
import "../styles/CreateMenu.css";

const BACKEND_URL = "http://localhost:5000"; // Add this line

// CreateMenuPage component for adding new food items
function CreateMenuPage({ menu: initialMenu = [], setMenu, onAddItem, onRemoveItem }) {
  const [itemName, setItemName] = useState('');
  const [itemPrice, setItemPrice] = useState('');
  const [itemImage, setItemImage] = useState(null);
  const [imagePreview, setImagePreview] = useState('');
  const [message, setMessage] = useState('');
  const [menu, setMenuState] = useState(initialMenu);

  // Always fetch menu after add/remove
  const fetchMenu = async () => {
    try {
      const token = localStorage.getItem("token");
      const user = JSON.parse(localStorage.getItem("user"));
      const res = await axios.get("/api/menu", {
        headers: {
          Authorization: token ? `Bearer ${token}` : undefined,
        },
        params: {
          userId: user?.id,
        },
      });
      setMenuState(res.data);
      if (setMenu) setMenu(res.data);
    } catch {
      setMenuState([]);
      if (setMenu) setMenu([]);
    }
  };

  useEffect(() => {
    fetchMenu();
    // eslint-disable-next-line
  }, []);

  // Handle image file selection
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setItemImage(file);
    if (file) {
      setImagePreview(URL.createObjectURL(file));
    } else {
      setImagePreview('');
    }
  };

  // Function to handle form submission for adding menu item
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    if (!itemName || !itemPrice || !itemImage) {
      setMessage('Please fill in all fields.');
      return;
    }
    const formData = new FormData();
    formData.append("name", itemName);
    formData.append("price", itemPrice);
    formData.append("image", itemImage);

    try {
      const token = localStorage.getItem("token");
      await axios.post("/api/menu", formData, {
        headers: {
          Authorization: token ? `Bearer ${token}` : undefined,
          "Content-Type": "multipart/form-data",
        },
      });
      setMessage('Menu item added successfully!');
      setItemName('');
      setItemPrice('');
      setItemImage(null);
      setImagePreview('');
      await fetchMenu(); // Always refresh menu after add
      if (onAddItem) await onAddItem();
    } catch (err) {
      setMessage('Failed to add menu item.');
    }
  };

  // Remove handler
  const handleRemove = async (id) => {
    if (window.confirm("Are you sure you want to remove this menu item?")) {
      await onRemoveItem(id);
      await fetchMenu(); // Always refresh menu after remove
    }
  };

  const getImageUrl = (image) => {
    if (!image) return "";
    let relPath = image.replace(/^\/?uploads\//, "");
    if (image.startsWith("http://") || image.startsWith("https://")) {
      return image;
    }
    if (image.startsWith("/uploads/") || image.startsWith("uploads/")) {
      const parts = relPath.split("/");
      if (parts.length > 1) {
        const filename = encodeURIComponent(parts.pop());
        return `${BACKEND_URL}/uploads/${parts.join("/")}/${filename}`;
      }
      return `${BACKEND_URL}/uploads/${encodeURIComponent(relPath)}`;
    }
    return `${BACKEND_URL}/uploads/${encodeURIComponent(image)}`;
  };

  // Add Analytics component here
  const AnalyticsInsights = () => (
    <div
      style={{
        margin: "2.5rem auto",
        background: "#fff",
        borderRadius: 8,
        boxShadow: "0 2px 12px rgba(0,0,0,0.07)",
        padding: "2rem",
        maxWidth: 600,
        color: "#222",
      }}
    >
      <h3 style={{ color: "#2575fc", marginBottom: "1.2rem" }}>Analytics & Insights</h3>
      <div style={{ marginBottom: "1.5rem" }}>
        <b>Most Viewed Categories/Dishes</b>
        <ul style={{ margin: "0.5rem 0 0 1.2rem" }}>
          <li>• Pizza (120 views)</li>
          <li>• Burgers (98 views)</li>
          <li>• Pasta (85 views)</li>
        </ul>
      </div>
      <div style={{ marginBottom: "1.5rem" }}>
        <b>Peak Scan Times</b>
        <ul style={{ margin: "0.5rem 0 0 1.2rem" }}>
          <li>• 12:00 PM - 2:00 PM</li>
          <li>• 7:00 PM - 9:00 PM</li>
        </ul>
      </div>
      <div>
        <b>Customer Interaction Heatmap</b>
        <div
          style={{
            marginTop: "0.7rem",
            width: "100%",
            height: 80,
            background:
              "linear-gradient(90deg, #f97316 30%, #facc15 60%, #4ade80 100%)",
            borderRadius: 6,
            position: "relative",
          }}
        >
          <span
            style={{
              position: "absolute",
              left: 10,
              top: 10,
              color: "#fff",
              fontWeight: "bold",
              fontSize: 12,
            }}
          >
            Low
          </span>
          <span
            style={{
              position: "absolute",
              right: 10,
              top: 10,
              color: "#fff",
              fontWeight: "bold",
              fontSize: 12,
            }}
          >
            High
          </span>
          {/* This is a placeholder. Replace with a real heatmap/chart for production. */}
        </div>
      </div>
    </div>
  );

  return (
    <div>
      <div className="main-menu">
        <div class="main-div">
          <div className="menu-header">
            <div className="form-div">
              <h1 className="header1">
                Create New Menu Item
              </h1>
              <form onSubmit={handleSubmit} className="menu-form" encType="multipart/form-data">
                {/* Item Name */}
                <div>
                  <label htmlFor="itemName" className="label">
                    Food Name
                  </label>
                  <input
                    type="text"
                    id="itemName"
                    value={itemName}
                    onChange={(e) => setItemName(e.target.value)}
                    className="input"
                    placeholder="e.g., Spicy Chicken Wings"
                    required
                  />
                </div>

                {/* Item Price */}
                <div>
                  <label htmlFor="itemPrice" className="label">
                    Price (₹)
                  </label>
                  <input
                    type="number"
                    id="itemPrice"
                    value={itemPrice}
                    onChange={(e) => setItemPrice(e.target.value)}
                    className="input"
                    placeholder="e.g., 299.00"
                    step="0.01" // Allow decimal values
                    required
                  />
                </div>

                {/* Image Upload */}
                <div>
                  <label htmlFor="itemImage" className="label">
                    Image
                  </label>
                  <input
                    type="file"
                    id="itemImage"
                    accept="image/*"
                    onChange={handleImageChange}
                    className="input"
                    required
                  />
                  {/* Removed image preview */}
                </div>
                {/* Submission Button */}
                <button
                  type="submit"
                  className="button"
                >
                  Add Menu Item
                </button>
                {/* Message display for success/error */}
                {message && (
                  <p className={`mt-4 text-center text-sm font-medium ${message.includes('successfully') ? 'text-green-600' : 'text-red-600'}`}>
                    {message}
                  </p>
                )}
              </form>
            </div>
          </div>
          {/* Created Items section */}
          <div className="created-item" style={{ position: "relative" }}>
            <h1
              className="header"
              style={{
                position: "sticky",
                top: 0,
                background: "#f7af7e",
                zIndex: 2,
                margin: 0,
                padding: "0.5rem 0 0.5rem 0"
              }}
            >
              Created Menu Items
            </h1>
            <div>
              <ul style={{ listStyle: "none", padding: 0 }}>
                {menu.length === 0 && <li>No menu items yet.</li>}
                {menu.map(item => (
                  <li key={item._id || item.id} style={{ margin: "1rem 0", display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                    <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
                      {item.image && <img src={getImageUrl(item.image)} alt={item.name} style={{ width: 50, height: 35, objectFit: "cover", borderRadius: 4 }} />}
                      <span>{item.name} - ₹{item.price}</span>
                    </div>
                    <button
                      className="button"
                      style={{ background: "#fff", color: "brown", border: "1px solid brown", width: "auto", padding: "0.2rem 1rem", marginLeft: "1rem" }}
                      onClick={() => handleRemove(item._id || item.id)}
                    >
                      Remove
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          </div>
          {/* Back to Menu button */}
          {/* <button
              onClick={onBackToMenu}
              className="button2"
            >
              Back to Menu
            </button> */}
        </div>
      </div>
      <AnalyticsInsights />
    </div >
  );
}

export default CreateMenuPage;
