import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/OrderPage.css";
import orderBg from "../img/orderbg.jpg";

const OrdersPage = ({ orders: initialOrders, setOrders }) => {
  const [orders, setLocalOrders] = useState(initialOrders);

  // Fetch orders from backend on mount
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get("/api/orders");
        setLocalOrders(res.data);
        if (setOrders) setOrders(res.data);
      } catch {
        setLocalOrders([]);
        if (setOrders) setOrders([]);
      }
    };
    fetchOrders();
  }, [setOrders]);

  // Mark order as completed and remove from UI and DB
  const handleComplete = async (id) => {
    try {
      await axios.patch(`/api/orders/${id}`, { status: "Completed" });
      // Remove only from local state, not from UI immediately
      setLocalOrders((prev) =>
        prev.map((order) =>
          order.id === id ? { ...order, status: "Completed" } : order
        )
      );
      if (setOrders)
        setOrders((prev) =>
          prev.map((order) =>
            order.id === id ? { ...order, status: "Completed" } : order
          )
        );
    } catch {
      // Optionally show error
    }
  };

  return (
    <div
      className="orders-section"
      style={{
        backgroundImage: `url('${orderBg}')`,
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center 2rem",
        backgroundSize: "cover",
        alignContent: "center",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        minHeight: "800px",
        color: "white"
      }}
    >
      <h3>Orders</h3>
      <ul
        style={{
          width: "100%",
          maxWidth: "500px",
          margin: "0 auto",
          textAlign: "center",
        }}
      >
        {orders.length === 0 && <li>No orders yet.</li>}
        {orders.map((order) => (
          <li
            key={order.id}
            style={{
              display: "flex",
              alignItems: "center",
              gap: "1rem",
              justifyContent: "center",
            }}
          >
            <span>
              Table {order.table}: {order.items.join(", ")} - {order.status}
            </span>
            {order.status !== "Completed" && (
              <input
                type="checkbox"
                title="Mark as Completed"
                onChange={() => handleComplete(order.id)}
                style={{ width: "18px", height: "18px" }}
              />
            )}
            {order.status === "Completed" && (
              <span style={{ color: "#28a745", fontWeight: "bold" }}>âœ“</span>
            )}
          </li>
        ))}
      </ul>
      {/* Placeholder for served orders */}
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          marginTop: "2rem",
          width: "100%",
          maxWidth: "500px",
          minHeight: "80px",
          background: "rgba(0,0,0,0.15)",
          borderRadius: "8px",
          color: "#fff",
          justifyContent: "center"
        }}
      >
        <span style={{ fontWeight: "bold", fontSize: "1.1rem" }}>
          Served Orders will appear here soon
        </span>
      </div>
    </div>
  );
};

export default OrdersPage;
