import React, { useState } from "react";
import "./CustomerMenu.css";

const BACKEND_URL = "http://localhost:5000"; // Make sure this matches your backend

const CustomerMenu = ({ menu = [], onBack, onPlaceOrder }) => {
  const [table, setTable] = useState("");

  const handleOrder = (item) => {
    if (!table) {
      alert("Please enter your table number.");
      return;
    }
    onPlaceOrder &&
      onPlaceOrder({
        items: [item.name],
        menuItemId: item._id || item.id,
        status: "Pending",
        table,
      });
  };

  // Fix: encode only the filename, not the whole path
  const getImageUrl = (image) => {
    if (!image) return "";
    if (image.startsWith("/uploads/") || image.startsWith("uploads/")) {
      // Remove leading slash if present
      const relPath = image.replace(/^\/?uploads\//, "");
      return `${BACKEND_URL}/uploads/${encodeURIComponent(relPath)}`;
    }
    if (image.startsWith("http://") || image.startsWith("https://")) {
      return image;
    }
    // fallback: treat as relative path
    return `${BACKEND_URL}/uploads/${encodeURIComponent(image)}`;
  };

  return (
    <div className="customer-menu-container">
      {/* Debug: Log menu data */}
      {console.log("Menu data:", menu)}
      <div style={{ textAlign: "center", marginBottom: "1rem" }}>
        <label>
          Table Number:{" "}
          <input
            type="text"
            value={table}
            onChange={(e) => setTable(e.target.value)}
            placeholder="Enter table number"
            style={{
              padding: "0.3rem",
              borderRadius: "4px",
              border: "1px solid #ccc",
            }}
          />
        </label>
      </div>
      <section className="menu-cards-section">
        {menu.length === 0 && (
          <div style={{ textAlign: "center", width: "100%" }}>
            No menu items available.
          </div>
        )}
        {menu.map((item) => {
          const imgUrl = getImageUrl(item.image);
          // Debug: log the image URL
          console.log("Image URL for", item.name, ":", imgUrl);
          return (
            <article key={item._id || item.id} className="menu-card">
              {item.image && (
                <img
                  src={imgUrl}
                  alt={item.name}
                  style={{
                    width: "180px",
                    height: "120px",
                    objectFit: "cover",
                    borderRadius: "8px",
                  }}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "https://via.placeholder.com/180x120?text=No+Image";
                  }}
                />
              )}
              {/* Show image field and URL for debugging */}
              <div style={{ fontSize: '0.8rem', color: '#a00', marginBottom: '0.5rem', wordBreak: 'break-all' }}>
                <div><b>image:</b> {String(item.image)}</div>
                <div><b>imgUrl:</b> {imgUrl}</div>
              </div>
              <h3>{item.name}</h3>
              <span>
                Price: <b>{item.price}/-</b>
              </span>
              <button
                className="order-now-btn"
                type="button"
                onClick={() => handleOrder(item)}
              >
                Order Now
              </button>
            </article>
          );
        })}
      </section>
      <button onClick={onBack} className="back-btn">
        Back
      </button>
    </div>
  );
};

export default CustomerMenu;
