import React from "react";
import "../styles/OrderPage.css";
// Use require or import for local images in React (Webpack)
// Place your image in src/assets or src/img for best results
import orderBg from "../img/orderbg.jpg";

const OrdersPage = ({ orders, setOrders }) => {
  const handleComplete = (id) => {
    setOrders((prev) =>
      prev
        .map((order) =>
          order.id === id ? { ...order, status: "Completed" } : order
        )
        .filter((order) => order.id !== id)
    );
  };

  return (
    <div
      className="orders-section"
      style={{
        backgroundImage: `url('${orderBg}')`,
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center 2rem",
        backgroundSize: "cover",
        alignContent: "center",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        minHeight: "800px",
      }}
    >
      <h3>Orders</h3>
      <ul
        style={{
          width: "100%",
          maxWidth: "500px",
          margin: "0 auto",
          textAlign: "center",
        }}
      >
        {orders.length === 0 && <li>No orders yet.</li>}
        {orders.map((order) => (
          <li
            key={order.id}
            style={{
              display: "flex",
              alignItems: "center",
              gap: "1rem",
              justifyContent: "center",
            }}
          >
            <span>
              Table {order.table}: {order.items.join(", ")} - {order.status}
            </span>
            {order.status !== "Completed" && (
              <input
                type="checkbox"
                title="Mark as Completed"
                onChange={() => handleComplete(order.id)}
                style={{ width: "18px", height: "18px" }}
              />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OrdersPage;
