import React, { useState } from "react";
import "./CustomerMenu.css";

const CustomerMenu = ({ menu, onBack, onPlaceOrder }) => {
  const [selectedItems, setSelectedItems] = useState([]);
  const [tableNumber, setTableNumber] = useState("");
  const [orderPlaced, setOrderPlaced] = useState(false);

  const handleSelect = (id) => {
    setSelectedItems((prev) =>
      prev.includes(id) ? prev.filter((item) => item !== id) : [...prev, id]
    );
  };

  const handleOrder = (e) => {
    e.preventDefault();
    if (!tableNumber || selectedItems.length === 0) return;
    const items = menu
      .filter((item) => selectedItems.includes(item.id))
      .map((item) => item.name);
    onPlaceOrder({ table: tableNumber, items });
    setOrderPlaced(true);
    setSelectedItems([]);
    setTableNumber("");
  };

  return (
    <div className="customer-menu-container">
      <div className="menu-list">
        <h2>Menu</h2>
        <ul>
          {menu.map((item) => (
            <li key={item.id}>
              <label>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item.id)}
                  onChange={() => handleSelect(item.id)}
                />
                {item.name} - â‚¹{item.price}
              </label>
            </li>
          ))}
        </ul>
        <button onClick={onBack} className="back-btn">
          Back
        </button>
      </div>
      <div className="order-form">
        <h3>Place Order</h3>
        {orderPlaced ? (
          <div className="order-success">Order placed! Thank you.</div>
        ) : (
          <form onSubmit={handleOrder}>
            <div>
              <label>
                Table Number:
                <input
                  type="number"
                  value={tableNumber}
                  onChange={(e) => setTableNumber(e.target.value)}
                  required
                  min="1"
                />
              </label>
            </div>
            <div>
              <label>
                Selected Items:
                <ul>
                  {menu
                    .filter((item) => selectedItems.includes(item.id))
                    .map((item) => (
                      <li key={item.id}>{item.name}</li>
                    ))}
                </ul>
              </label>
            </div>
            <button
              type="submit"
              disabled={selectedItems.length === 0 || !tableNumber}
            >
              Order
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default CustomerMenu;
