import React, { useState } from "react";
import "./Dashboard.css";
import NavBar from "./NavBar";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from "chart.js";
import CustomerMenu from "./CustomerMenu";
import CreateMenuPage from "./CreateMenuPage";

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const Dashboard = ({ menu, setMenu, orders, setOrders }) => {
  const [activeSection, setActiveSection] = useState("menu");

  // Chart data (can be removed if not needed)
  // const totalOrders = orders.length;
  // const pendingOrders = orders.filter(o => o.status === "Pending").length;
  // const completedOrders = orders.filter(o => o.status === "Completed").length;
  // const data = {
  //   labels: ["Total", "Pending", "Completed"],
  //   datasets: [
  //     {
  //       label: "Orders",
  //       data: [totalOrders, pendingOrders, completedOrders],
  //       backgroundColor: ["#007bff", "#ffc107", "#28a745"],
  //     },
  //   ],
  // };
  // const options = {
  //   responsive: true,
  //   plugins: {
  //     legend: { display: false },
  //   },
  //   scales: {
  //     y: { beginAtZero: true, ticks: { stepSize: 1 } },
  //   },
  // };

  return (
    <div className="dashboard-container">
      <NavBar
        activeSection={activeSection}
        onMenu={() => setActiveSection("menu")}
        onQRCode={() => setActiveSection("qrcode")}
        onOrders={() => setActiveSection("customerMenu")}
      />
      <h2>Hotel Admin Dashboard</h2>
      {/* Order Analytics removed */}
      {/* <div className="order-analytics">
        <div>Total Orders: <b>{totalOrders}</b></div>
        <div>Pending Orders: <b>{pendingOrders}</b></div>
        <div>Completed Orders: <b>{completedOrders}</b></div>
      </div>
      <div style={{ maxWidth: 400, margin: "2rem auto" }}>
        <Bar data={data} options={options} />
      </div> */}
      {activeSection === "menu" && (
        <CreateMenuPage menu={menu} setMenu={setMenu} />
      )}
      {activeSection === "customerMenu" && (
        <CustomerMenu />
      )}
    </div>
  );
};

export default Dashboard;
