import React, { useState } from "react";
import NavBar from "./NavBar";
import "../styles/CreateMenu.css";
const CreateMenuPage = ({ menu, setMenu, onGenerateOrders }) => {
  const [newItem, setNewItem] = useState({ name: "", price: "" });

  const handleAddMenu = (e) => {
    e.preventDefault();
    if (!newItem.name || !newItem.price) return;
    setMenu([
      ...menu,
      { id: Date.now(), name: newItem.name, price: Number(newItem.price) },
    ]);
    setNewItem({ name: "", price: "" });
  };

  const handleDeleteMenu = (id) => {
    setMenu(menu.filter((item) => item.id !== id));
  };

  return (
    <div>
      <NavBar>
        <button className="generate-orders-btn" onClick={onGenerateOrders}>
          Generate Menu Orders
        </button>
      </NavBar>
      <div className="menu-section">
        <h3>Menu</h3>
        <ul>
          {menu.map((item) => (
            <li key={item.id}>
              {item.name} - â‚¹{item.price}
              <button onClick={() => handleDeleteMenu(item.id)}>Delete</button>
            </li>
          ))}
        </ul>
        <form onSubmit={handleAddMenu} className="add-menu-form">
          <input
            type="text"
            placeholder="Item Name"
            value={newItem.name}
            onChange={(e) =>
              setNewItem({ ...newItem, name: e.target.value })
            }
            required
          />
          <input
            type="number"
            placeholder="Price"
            value={newItem.price}
            onChange={(e) =>
              setNewItem({ ...newItem, price: e.target.value })
            }
            required
          />
          <button type="submit">Add Item</button>
        </form>
      </div>
    </div>
  );
};

export default CreateMenuPage;
