import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/CustomerMenu.css";

const BACKEND_URL = "http://localhost:5000";

const OrdersTable = ({ orders }) => (
  <div style={{ maxWidth: 700, margin: "2rem auto", background: "#fff", borderRadius: 8, boxShadow: "0 2px 12px rgba(0,0,0,0.07)", padding: "1.5rem" }}>
    <h2 style={{ textAlign: "center", color: "#2575fc", marginBottom: "1rem" }}>Your Orders</h2>
    <table style={{ width: "100%", borderCollapse: "collapse", fontSize: "1rem" }}>
      <thead>
        <tr style={{ background: "#f7af7e", color: "#fff" }}>
          <th style={{ padding: "0.7rem", borderRadius: "6px 0 0 0" }}>Date</th>
          <th style={{ padding: "0.7rem" }}>Table</th>
          <th style={{ padding: "0.7rem" }}>Items</th>
          <th style={{ padding: "0.7rem" }}>Status</th>
        </tr>
      </thead>
      <tbody>
        {orders.length === 0 && (
          <tr>
            <td colSpan={4} style={{ textAlign: "center", padding: "1.2rem" }}>No orders yet.</td>
          </tr>
        )}
        {orders.map((order, idx) => (
          <tr key={order._id || order.id || idx} style={{ borderBottom: "1px solid #eee" }}>
            <td style={{ padding: "0.7rem", color: "#444" }}>
              {order.createdAt ? new Date(order.createdAt).toLocaleString() : "-"}
            </td>
            <td style={{ padding: "0.7rem", color: "#444" }}>{order.table || "-"}</td>
            <td style={{ padding: "0.7rem", color: "#444" }}>
              {Array.isArray(order.items) ? order.items.join(", ") : order.items}
            </td>
            <td style={{ padding: "0.7rem", color: order.status === "Completed" ? "#22c55e" : "#f97316", fontWeight: "bold" }}>
              {order.status || "-"}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

const CustomerMenu = ({ menu: initialMenu = [], onBack, onPlaceOrder, userId }) => {
  const [menu, setMenu] = useState(initialMenu);
  const [table, setTable] = useState("");
  const [bookOpen, setBookOpen] = useState(false);
  const [orders, setOrders] = useState([]);

  // Fetch menu for customer if userId is provided
  useEffect(() => {
    if (userId) {
      axios.get("/api/menu", { params: { userId } })
        .then(res => setMenu(res.data))
        .catch(() => setMenu([]));
    }
    // Fetch orders for this table if table is set
    if (table) {
      axios.get("/api/orders", { params: { table } })
        .then(res => {
          let arr = Array.isArray(res.data) ? res.data : (res.data.orders || []);
          setOrders(arr.filter(o => o.table === table));
        })
        .catch(() => setOrders([]));
    }
  }, [userId, table]);

  const handleOrder = async (item) => {
    if (!table) {
      alert("Please enter your table number.");
      return;
    }
    const confirmOrder = window.confirm(`Place order for "${item.name}" at table ${table}?`);
    if (!confirmOrder) return;

    // Send order to backend
    try {
      await axios.post("/api/orders", {
        items: [item.name],
        menuItemId: item._id || item.id,
        status: "Pending",
        table,
      });
      alert("Order placed successfully!");
      // Refresh orders after placing
      axios.get("/api/orders", { params: { table } })
        .then(res => {
          let arr = Array.isArray(res.data) ? res.data : (res.data.orders || []);
          setOrders(arr.filter(o => o.table === table));
        })
        .catch(() => setOrders([]));
    } catch (err) {
      alert("Failed to place order. Please try again.");
    }

    // If you want to call onPlaceOrder for local state/UI update, keep this:
    onPlaceOrder &&
      onPlaceOrder({
        items: [item.name],
        menuItemId: item._id || item.id,
        status: "Pending",
        table,
      });
  };

  // Add debug logging and show image URL in UI
  const getImageUrl = (image) => {
    if (!image) return null;
    if (image.startsWith("http://") || image.startsWith("https://")) {
      return image;
    }
    let relPath = image.replace(/^\/?uploads\//, "");
    const parts = relPath.split("/");
    const filename = encodeURIComponent(parts.pop());
    const folder = parts.length ? parts.join("/") + "/" : "";
    const url = `${BACKEND_URL}/uploads/${folder}${filename}`;
    return url;
  };

  // Divide menu items equally among the three pages
  const pageCount = 3;
  const perPage = Math.ceil(menu.length / pageCount);
  const menuPages = [
    menu.slice(0, perPage), // cover-back (first page)
    menu.slice(perPage, perPage * 2), // final-page (second page)
    menu.slice(perPage * 2), // page-1-back (third page)
  ];

  return (
    <div className="customer-menu-container">
      {/* Book Opening Effect */}
      <div className="app-container">
        <div
          className={`book${bookOpen ? " open" : ""}`}
          onClick={() => setBookOpen((open) => !open)}
          style={{ cursor: "pointer" }}
        >
          <div className="final-page">
            <h2>Menu</h2>
            <ul style={{ listStyle: "none", padding: 0, margin: 0, textAlign: "center", display: "flex", flexDirection: "column", gap: "1.2rem" }}>
              {menuPages[1].map((item) => (
                <li key={item._id || item.id}>{item.name}</li>
              ))}
            </ul>
          </div>
          <div className="page-1">
            <div className="face page-1-front">
              {/* You can add content for the right page if needed */}
            </div>
            <div className="face page-1-back">
              <h2>Menu</h2>
              <ul style={{ listStyle: "none", padding: 0, margin: 0, textAlign: "center", display: "flex", flexDirection: "column", gap: "1.2rem" }}>
                {menuPages[2].map((item) => (
                  <li key={item._id || item.id}>{item.name}</li>
                ))}
              </ul>
            </div>
          </div>
          <div className="cover">
            <div className="face cover-front">
              <div className="cover-title">The Magic of CSS</div>
              <p>By A. Developer</p>
            </div>
            <div className="face cover-back">
              <h2>Menu</h2>
              <ul style={{ listStyle: "none", padding: 0, margin: 0, textAlign: "center", display: "flex", flexDirection: "column", gap: "1.2rem" }}>
                {menuPages[0].map((item) => (
                  <li key={item._id || item.id}>{item.name}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
      <div style={{ textAlign: "center", marginBottom: "1rem" }}>
        <label>
          Table Number:{" "}
          <input
            type="text"
            value={table}
            onChange={(e) => setTable(e.target.value)}
            placeholder="Enter table number"
            style={{
              padding: "0.3rem",
              borderRadius: "4px",
              border: "1px solid #ccc",
            }}
          />
        </label>
      </div>
      <section className="menu-cards-section">
        {menu.length === 0 && (
          <div style={{ textAlign: "center", width: "100%" }}>
            No menu items available.
          </div>
        )}
        {menu.map((item) => {
          const imgUrl = getImageUrl(item.image);
          return (
            <article key={item._id || item.id} className="menu-card">
              <img
                src={imgUrl || "https://placehold.co/180x120?text=No+Image"}
                alt={item.name}
                style={{
                  width: "180px",
                  height: "120px",
                  objectFit: "cover",
                  borderRadius: "8px",
                }}
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = "https://placehold.co/180x120?text=No+Image";
                }}
              />
              <h3>{item.name}</h3>
              <span>
                Price: <b>{item.price}/-</b>
              </span>
              <button
                className="order-now-btn"
                type="button"
                onClick={() => handleOrder(item)}
              >
                Order Now
              </button>
            </article>
          );
        })}
      </section>
      {/* Orders Table */}
      <OrdersTable orders={orders} />
    </div>
  );
};

export default CustomerMenu;
