import React, { useState, useEffect } from "react";
import axios from "axios";
import HomePage from "./components/HomePage";
import CustomerMenu from "./components/CustomerMenu";
import LoginPage from "./components/LoginPage";
import Dashboard from "./components/Dashboard";
import "./styles/LoginSignup.css";

function App() {
  const [page, setPage] = useState("home");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [menu, setMenu] = useState([]);
  const [orders, setOrders] = useState([]);

  // Auto-login if token exists
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) setIsLoggedIn(true);
  }, []);

  // Fetch menu from backend on mount
  useEffect(() => {
    axios
      .get("/api/menu")
      .then((res) => setMenu(res.data))
      .catch(() => setMenu([]));
  }, []);

  // Add menu item via backend
  const handleAddMenuItem = async (item) => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.post("/api/menu", item, {
        headers: {
          Authorization: token ? `Bearer ${token}` : undefined,
        },
      });
      setMenu((prev) => [...prev, res.data]);
    } catch (err) {
      // handle error (e.g., show message)
    }
  };

  const handlePlaceOrder = (order) => {
    setOrders((prev) => [
      ...prev,
      { ...order, id: Date.now(), status: "Pending" },
    ]);
  };

  // Handler to login and redirect to dashboard
  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
    setPage("home"); // This will trigger Dashboard due to isLoggedIn
  };

  // If logged in, always show Dashboard
  if (isLoggedIn) {
    return (
      <Dashboard
        menu={menu}
        setMenu={setMenu}
        orders={orders}
        setOrders={setOrders}
        onAddMenuItem={handleAddMenuItem}
      />
    );
  }

  // Show CustomerMenu if page is "menu"
  if (page === "menu") {
    return (
      <CustomerMenu
        menu={menu}
        onBack={() => setPage("home")}
        onPlaceOrder={handlePlaceOrder}
      />
    );
  }

  // Show HomePage, LoginPage, or SignupPage
  return (
    <div className="App">
      {page === "home" && (
        <HomePage
          onViewMenu={() => setPage("menu")}
          onLogin={() => setPage("LoginPage")}
          onSignup={() => setPage("SignupPage")}
        />
      )}
      {page === "LoginPage" && (
        <LoginPage
          onLogin={handleLoginSuccess}
        />
      )}
      {page === "SignupPage" && (
        <LoginPage
          signupMode={true}
          onLogin={handleLoginSuccess}
        />
      )}
    </div>
  );
}

export default App;
