import React from "react";
import LoginPage from "./LoginPage";
import "../styles/HomePage.css";

const HomePage = ({ onViewMenu, onLogin, onSignup, onSwitch }) => (
  <div className="home-center-container" style={{
    minHeight: "100vh",
    background: "linear-gradient(120deg, #f8fafc 0%, #e0e7ef 100%), url('../img/bg.jpg') no-repeat center center fixed",
    backgroundSize: "cover",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "flex-start"
  }}>
    {/* Top Navbar */}
    <div className="home-top-navbar">
      <div className="app-logo">
        {/* Replace with your logo image if available */}
        <span>QR Menu</span>
      </div>
      <nav className="home-navbar">
        <button className="view-menu-btn" onClick={onViewMenu}>
          View Menu
        </button>
      </nav>
    </div>

    {/* Center: Login/Signup */}
    <div className="home-login-center">
      <LoginPage onSwitch={onSwitch} onLogin={onLogin} />
    </div>
  </div>
);

export default HomePage;
